function ic_apply_range_slider(i){if(typeof i.ionRangeSlider!=="function"){return}var e=1;var r=i.data("unit");var a=i.data("min");var t=i.data("max");var n=i.data("current-min");var o=i.data("current-max");if(t-a<2){e=.1}if(n===o){if(n===a){o=o+e}else{n=n-e}}i.ionRangeSlider({type:"double",min:a,max:t,from:n,to:o,step:e,postfix:" "+r,grid:true,hide_min_max:true,onFinish:function(){var e=i.closest("form");e.submit();i.closest(".product_size_filter").addClass("active")}})}jQuery(document).ready(function(i){i("body").on("reload",".product-size-filter-container",function(){var e=i(this);e.removeClass("toReload");e.find(".ic-range-slider").each(function(){var e=i(this);ic_apply_range_slider(e)})});i(".product-size-filter-container.toReload").trigger("reload")});